import React, { useState, useEffect } from 'react';

function AddDoctorForm({ doctor, onSave, onCancel, isEditMode = false }) {
  const [doctorImage, setDoctorImage] = useState(null);
  const [doctorName, setDoctorName] = useState("");
  const [specialties, setSpecialties] = useState([]);
  const [email, setEmail] = useState("");
  const [degree, setDegree] = useState("");
  const [password, setPassword] = useState("");
  const [experience, setExperience] = useState("");
  const [isDiscardModalOpen, setIsDiscardModalOpen] = useState(false);

  const specialtyOptions = [
    "General Physician",
    "Gynecologist",
    "Dermatologist",
    "Pediatrician",
    "Neurologist",
    "Gastroenterologist",
    // Add more specializations if needed
  ];

  useEffect(() => {
    if (doctor) {
      setDoctorImage(doctor.image);
      setDoctorName(doctor.name);
      setSpecialties(doctor.specialties || []);
      setEmail(doctor.email || "");
      setDegree(doctor.degree || "");
      setPassword(""); // Reset password for security reasons
      setExperience(doctor.experience || "");
    } else {
      // Reset form if there's no doctor (for adding a new one)
      setDoctorImage(null);
      setDoctorName("");
      setSpecialties([]);
      setEmail("");
      setDegree("");
      setPassword("");
      setExperience("");
    }
  }, [doctor]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setDoctorImage(URL.createObjectURL(file));
  };

  const handleSave = () => {
    const doctorData = {
      name: doctorName,
      specialties,
      email,
      degree,
      password,
      experience,
      image: doctorImage,
    };
    onSave(doctorData); // Trigger the save action
  };

  const handleDiscardChanges = () => {
    setIsDiscardModalOpen(true); // Open discard confirmation modal
  };

  const handleCloseDiscardModal = () => {
    setIsDiscardModalOpen(false); // Close discard modal
  };

  const handleConfirmDiscard = () => {
    handleCloseDiscardModal();  // Close the discard modal

    if (onCancel) {
      onCancel();  // If onCancel is provided, call it to close the main modal as well
    }
  };

  const handleSpecialtyChange = (event) => {
    const selectedValue = event.target.value;
    if (!specialties.includes(selectedValue)) {
      const updatedSpecialties = [...specialties, selectedValue];
      setSpecialties(updatedSpecialties);
    }
    event.target.value = ""; // Reset the dropdown after selection
  };

  const removeSpecialty = (specialty) => {
    const updatedSpecialties = specialties.filter((item) => item !== specialty);
    setSpecialties(updatedSpecialties);
  };

  return (
    <>
      <section className="flex flex-col bg-white border rounded-lg p-8 w-full">
        {/* Upload Doctor Picture */}
        <div className="flex items-center mb-4">
          <label htmlFor="doctorImage" className="cursor-pointer">
            <div className="w-14 h-14 bg-gray-200 rounded-full flex items-center justify-center">
              {doctorImage ? (
                <img src={doctorImage} alt="Doctor" className="w-14 h-14 rounded-full object-cover" />
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  className="w-8 h-8 text-gray-400"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 4.5C13.38 4.5 14.5 3.38 14.5 2C14.5 0.62 13.38 -0.5 12 -0.5C10.62 -0.5 9.5 0.62 9.5 2C9.5 3.38 10.62 4.5 12 4.5ZM5 20.5C5 17 8 14 12 14C16 14 19 17 19 20.5C19 20.92 18.92 21.31 18.79 21.67C17.34 21.18 15.73 21 14 21C11.24 21 8.73 21.27 6.5 21.81C6.18 21.07 6 20.32 6 19.5C6 18.12 5 17.5 5 20.5Z"
                  />
                </svg>
              )}
            </div>
            <input
              type="file"
              id="doctorImage"
              accept="image/*"
              className="hidden"
              onChange={handleImageUpload}
            />
          </label>
          <span className="ml-4 text-gray-500">Upload doctor picture</span>
        </div>

        {/* Doctor form */}
        <form className="w-full grid grid-cols-2 gap-4 mb-6">
          <div>
            <label className="block mb-2">Doctor's Name</label>
            <input
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="Name"
              value={doctorName}
              onChange={(e) => setDoctorName(e.target.value)}
            />
          </div>

          {/* Multiple Specialties Section */}
          <div className="col-span-2">
            <label className="block mb-2">Specialties</label>

            {/* Selected Specialties Display */}
            <div className="flex flex-wrap gap-2 mb-2">
              {specialties.map((specialty, index) => (
                <div
                  key={index}
                  className="flex items-center bg-indigo-100 text-indigo-700 p-2 rounded-full"
                >
                  <span>{specialty}</span>
                  <button
                    type="button"
                    className="ml-2 text-red-500 hover:text-red-700"
                    onClick={() => removeSpecialty(specialty)}
                  >
                    &#x2715;
                  </button>
                </div>
              ))}
            </div>

            {/* Dropdown for Selecting Specialties */}
            <select
              value=""
              onChange={handleSpecialtyChange}
              className="w-full p-2 border rounded-md text-gray-400"
            >
              <option value="" disabled>Select Specialization</option>
              {specialtyOptions.map((specialty, index) => (
                <option key={index} value={specialty}>
                  {specialty}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block mb-2">Doctor's Email</label>
            <input
              type="email"
              className="w-full p-2 border rounded-md"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div>
            <label className="block mb-2">Degree</label>
            <input
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="Degree"
              value={degree}
              onChange={(e) => setDegree(e.target.value)}
            />
          </div>

          <div>
            <label className="block mb-2">
              {isEditMode ? 'Change Password' : 'Set Password'}
            </label>
            <input
              type="password"
              className="w-full p-2 border rounded-md"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <div>
            <label className="block mb-2">Experience</label>
            <input
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="1 Year"
              value={experience}
              onChange={(e) => setExperience(e.target.value)}
            />
          </div>
        </form>

        {/* Buttons */}
        <div className="flex gap-3">
          <button
            type="button"
            className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-full"
            onClick={handleSave}
          >
            {doctor ? 'Save Changes' : 'Add Doctor'}
          </button>
          {doctor && (
            <button
              type="button"
              className="px-4 py-2 bg-red-500 hover:bg-red-800 text-white rounded-full"
              onClick={handleDiscardChanges}
            >
              Discard Changes
            </button>
          )}
        </div>
      </section>

      {/* Discard Confirmation Modal */}
      {isDiscardModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <p className="mb-4">Are you sure you want to discard the unsaved changes?</p>
            <div className="flex justify-end gap-2">
              <button
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-full"
                onClick={handleCloseDiscardModal}
              >
                Close
              </button>
              <button
                className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-full"
                onClick={handleConfirmDiscard}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default AddDoctorForm;
