import React, { useState, useEffect } from 'react';
import AddRoom from './AddRoom';  // Import the AddRoom component

function AddFacilityForm({ facility, onCancel }) {  // Accept onCancel to trigger parent modal close
  const [rooms, setRooms] = useState([]);
  const [facilityName, setFacilityName] = useState("");
  const [isDiscardModalOpen, setIsDiscardModalOpen] = useState(false);  // Track discard confirmation modal

  useEffect(() => {
    if (facility) {
      // Populate the form fields with the passed facility data
      setFacilityName(facility.name);
      setRooms(facility.rooms || []);
    }
  }, [facility]);

  const addRoom = () => {
    setRooms([...rooms, { id: rooms.length + 1, specializations: [] }]);
  };

  const removeRoom = (roomId) => {
    setRooms(rooms.filter((room) => room.id !== roomId));
  };

  const handleRoomChange = (roomId, value) => {
    const updatedRooms = rooms.map((room) =>
      room.id === roomId ? { ...room, specializations: value } : room
    );
    setRooms(updatedRooms);
  };

  const handleDiscardChanges = () => {
    setIsDiscardModalOpen(true); // Show discard confirmation modal
  };

  const handleCloseDiscardModal = () => {
    setIsDiscardModalOpen(false); // Close discard modal
  };

  const handleConfirmDiscard = () => {
    setIsDiscardModalOpen(false); // Close the discard modal
    if (onCancel) onCancel(); // Trigger the parent cancel handler to close the main modal
  };

  return (
    <>
      <section className="flex flex-col bg-white border rounded-lg p-8 w-full min-w-[450px] max-w-[800px]">
        {/* Facility Name */}
        <div>
          <label htmlFor="facility-name" className="block mb-2">Facility Name</label>
          <input
            id="facility-name"
            name="facility-name"
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Name"
            value={facilityName}
            onChange={(e) => setFacilityName(e.target.value)}
          />
        </div>

        {/* Dynamically rendered rooms */}
        {rooms.map((room) => (
          <AddRoom
            key={room.id}
            room={room}
            onRemoveRoom={removeRoom}
            onRoomChange={handleRoomChange}
          />
        ))}

        {/* Buttons */}
        <div className="flex gap-3 mt-4">
          <button
            type="button"
            className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-full"
            onClick={addRoom}
          >
            Add Room
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-full"
          >
            {facility ? 'Save Changes' : 'Add Facility'}
          </button>
          {facility && (
            <button
              type="button"
              className="px-4 py-2 bg-red-500 hover:bg-red-800 text-white rounded-full"
              onClick={handleDiscardChanges}  // Show discard confirmation modal
            >
              Discard Changes
            </button>
          )}
        </div>
      </section>

      {/* Discard Confirmation Modal */}
      {isDiscardModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <p className="mb-4">Are you sure you want to discard the unsaved changes?</p>
            <div className="flex justify-end gap-2">
              <button
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-full"
                onClick={handleCloseDiscardModal}
              >
                Close
              </button>
              <button
                className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-full"
                onClick={handleConfirmDiscard}  // Confirm discard and close the form modal
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default AddFacilityForm;
