import React, { useState } from 'react';

const facilities = [
  { id: 1, name: "Facility 1", address: "123 Main St", image: "https://cdn.builder.io/api/v1/image/assets/TEMP/64f86958cbb3ab0d42bdcfc7e147c123b5342f5eae207c89f0bbb848ea0d1c67?placeholderIfAbsent=true&apiKey=8f0d2cc9bf764b679624a1d309a49f82" },
  { id: 2, name: "Facility 2", address: "456 Elm St", image: "https://cdn.builder.io/api/v1/image/assets/TEMP/64f86958cbb3ab0d42bdcfc7e147c123b5342f5eae207c89f0bbb848ea0d1c67?placeholderIfAbsent=true&apiKey=8f0d2cc9bf764b679624a1d309a49f82" },
  { id: 3, name: "Facility 3", address: "789 Oak St", image: "https://cdn.builder.io/api/v1/image/assets/TEMP/64f86958cbb3ab0d42bdcfc7e147c123b5342f5eae207c89f0bbb848ea0d1c67?placeholderIfAbsent=true&apiKey=8f0d2cc9bf764b679624a1d309a49f82" },
  // Add more facility data as needed...
];

function FacilityGrid({ onEdit }) {
  return (
    <div className="mt-8 bg-white p-6 shadow rounded-lg w-full max-w-[800px]">
      <h3 className="text-lg font-medium text-gray-900 mb-4">All Facilities</h3>
      <ul className="divide-y divide-gray-200">
        {facilities.map((facility) => (
          <li key={facility.id} className="flex justify-between items-center py-3">
            <div className="flex items-center">
              <img
                src={facility.image}
                alt={facility.name}
                className="w-10 h-10 rounded-full object-cover mr-3"
              />
              <span className="text-sm font-medium text-gray-900">{facility.name}</span>
            </div>
            <div className="flex gap-2">
              <button
                className="px-4 py-2 text-sm bg-indigo-500 text-white rounded-full hover:bg-indigo-600"
                onClick={() => onEdit(facility)}  // Pass the entire facility data
              >
                Edit
              </button>
              <button className="px-4 py-2 text-sm bg-red-500 text-white rounded-full hover:bg-red-600">
                Remove
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FacilityGrid;
