import React, { useState, useEffect } from 'react';
import ManageDoctors from './ManageDoctors';

function AddDoctorForm({ doctor, onSave, onCancel, isEditMode = false }) {
  const [doctorName, setDoctorName] = useState("");
  const [specialty, setSpecialty] = useState("Specialization");
  const [email, setEmail] = useState("");
  const [degree, setDegree] = useState("");
  const [password, setPassword] = useState("");
  const [experience, setExperience] = useState("");
  const [isDiscardModalOpen, setIsDiscardModalOpen] = useState(false);

  useEffect(() => {
    if (doctor) {
      setDoctorName(doctor.name);
      setSpecialty(doctor.specialty);
      setEmail(doctor.email || "");
      setDegree(doctor.degree || "");
      setPassword(""); // Reset password for security reasons
      setExperience(doctor.experience || "");
    } else {
      // Reset form if there's no doctor (for adding a new one)
      setDoctorName("");
      setSpecialty("Specialization");
      setEmail("");
      setDegree("");
      setPassword("");
      setExperience("");
    }
  }, [doctor]);

  const handleSave = () => {
    const doctorData = {
      name: doctorName,
      specialty,
      email,
      degree,
      password,
      experience,
    };
    onSave(doctorData); // Trigger the save action
  };

  const handleDiscardChanges = () => {
    setIsDiscardModalOpen(true); // Open discard confirmation modal
  };

  const handleCloseDiscardModal = () => {
    setIsDiscardModalOpen(false); // Close discard modal
  };

  const handleConfirmDiscard = () => {
    handleCloseDiscardModal();  // Close the discard modal

    if (onCancel) {
      onCancel();  // If onCancel is provided, call it to close the main modal as well
    }
  };

  return (
    <>
      <section className="flex flex-col bg-white border rounded-lg p-8 w-full">
        {/* Doctor form */}
        <form className="grid grid-cols-2 gap-4 mb-6">
          <div>
            <label className="block mb-2">Doctor's Name</label>
            <input
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="Name"
              value={doctorName}
              onChange={(e) => setDoctorName(e.target.value)}
            />
          </div>
          <div>
            <label className="block mb-2">Specialty</label>
            <select
              value={specialty}
              onChange={(e) => setSpecialty(e.target.value)}
              className={`w-full p-2 border rounded-md ${specialty === "Specialization" ? 'text-gray-400': 'text-black'}`}
            >
              <option value="Specialization" disabled>
                Specialization
              </option>
              <option value="General Physician">General Physician</option>
              <option value="Gynecologist">Gynecologist</option>
              <option value="Dermatologist">Dermatologist</option>
              <option value="Pediatrician">Pediatrician</option>
              <option value="Neurologist">Neurologist</option>
              <option value="Gastroenterologist">Gastroenterologist</option>
            </select>
          </div>
          <div>
            <label className="block mb-2">Doctor's Email</label>
            <input
              type="email"
              className="w-full p-2 border rounded-md"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label className="block mb-2">Degree</label>
            <input
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="Degree"
              value={degree}
              onChange={(e) => setDegree(e.target.value)}
            />
          </div>
          <div>
            {/* Conditionally render "Set Password" or "Change Password" */}
            <label className="block mb-2">
              {isEditMode ? 'Change Password' : 'Set Password'}
            </label>
            <input
              type="password"
              className="w-full p-2 border rounded-md"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div>
            <label className="block mb-2">Experience</label>
            <input
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="1 Year"
              value={experience}
              onChange={(e) => setExperience(e.target.value)}
            />
          </div>
        </form>
        {/* Buttons */}
        <div className="flex gap-3">
          <button
            type="button"
            className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-full"
            onClick={handleSave}
          >
            {doctor ? 'Save Changes' : 'Add Doctor'}
          </button>
          {/* Discard Changes Button */}
          {doctor && (
            <button
              type="button"
              className="px-4 py-2 bg-red-500 hover:bg-red-800 text-white rounded-full"
              onClick={handleDiscardChanges}
            >
              Discard Changes
            </button>
          )}
        </div>
      </section>

      {/* Discard Confirmation Modal */}
      {isDiscardModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <p className="mb-4">Are you sure you want to discard the unsaved changes?</p>
            <div className="flex justify-end gap-2">
              <button
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-full"
                onClick={handleCloseDiscardModal}
              >
                Close
              </button>
              <button
                className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-full"
                onClick={handleConfirmDiscard}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default AddDoctorForm;
