import React, { useState } from 'react';
import AddDoctorForm from './AddDoctorForm';
import DoctorGrid from './DoctorGrid';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';

function ManageDoctors() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [refreshGrid, setRefreshGrid] = useState(false);

  // Function to handle form save (add or edit doctor)
  const handleSaveDoctor = (doctorData) => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
    setRefreshGrid(true); // Trigger grid refresh after adding a doctor
  };

  // Close the modal without saving
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
  };

  return (
    <div className="flex-1 flex flex-col min-h-screen">
      <Header />
      <div className="flex w-full min-h-screen bg-gray-50">
        <Sidebar className="w-[289px] max-w-[289px] max-md:w-[200px] min-w-[150px]" />
        <main className="flex-1 px-8 py-4 w-full">
          <div className="flex flex-col gap-2">
            {/* Add Doctor Form - Only shows for adding a new doctor */}
            <div className="min-w-[700px] max-w-[1000px]">
              <AddDoctorForm onSave={handleSaveDoctor} onCancel={handleCloseModal} />
            </div>
            <div className="min-w-[900px] max-w-[1200px]">
              <DoctorGrid refresh={refreshGrid} setRefreshGrid={setRefreshGrid} />
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default ManageDoctors;
