import React, { useState, useEffect } from 'react';

function AddDoctorForm({ doctor, onSave, isEditMode = false, onCancel }) {
  const [doctorImage, setDoctorImage] = useState(null);
  const [doctorName, setDoctorName] = useState("");
  const [specialty, setSpecialty] = useState("Specialization");
  const [email, setEmail] = useState("");
  const [degree, setDegree] = useState("");
  const [password, setPassword] = useState("");
  const [address, setAddress] = useState("");
  const [experience, setExperience] = useState("");
  const [about, setAbout] = useState("");

  useEffect(() => {
    if (doctor) {
      setDoctorImage(doctor.image);
      setDoctorName(doctor.name);
      setSpecialty(doctor.specialty);
      setEmail(doctor.email || "");
      setDegree(doctor.degree || "");
      setPassword(""); // Reset password for security reasons
      setAddress(doctor.address || "");
      setExperience(doctor.experience || "");
      setAbout(doctor.about || "");
    } else {
      // Reset form if there's no doctor (for adding a new one)
      setDoctorImage(null);
      setDoctorName("");
      setSpecialty("Specialization");
      setEmail("");
      setDegree("");
      setPassword("");
      setAddress("");
      setExperience("");
      setAbout("");
    }
  }, [doctor]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setDoctorImage(URL.createObjectURL(file));
  };

  const handleSave = () => {
    const doctorData = {
      name: doctorName,
      specialty,
      email,
      degree,
      password,
      address,
      experience,
      about,
      image: doctorImage,
    };
    onSave(doctorData); // Trigger the save action
  };

  return (
    <section className="flex flex-col bg-white border rounded-lg p-8 w-full">
      {/* Upload Doctor Picture */}
      <div className="flex items-center mb-4">
        <label htmlFor="doctorImage" className="cursor-pointer">
          <div className="w-14 h-14 bg-gray-200 rounded-full flex items-center justify-center">
            {doctorImage ? (
              <img src={doctorImage} alt="Doctor" className="w-14 h-14 rounded-full object-cover" />
            ) : (
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                className="w-8 h-8 text-gray-400"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 4.5C13.38 4.5 14.5 3.38 14.5 2C14.5 0.62 13.38 -0.5 12 -0.5C10.62 -0.5 9.5 0.62 9.5 2C9.5 3.38 10.62 4.5 12 4.5ZM5 20.5C5 17 8 14 12 14C16 14 19 17 19 20.5C19 20.92 18.92 21.31 18.79 21.67C17.34 21.18 15.73 21 14 21C11.24 21 8.73 21.27 6.5 21.81C6.18 21.07 6 20.32 6 19.5C6 18.12 5 17.5 5 20.5Z"
                />
              </svg>
            )}
          </div>
          <input
            type="file"
            id="doctorImage"
            accept="image/*"
            className="hidden"
            onChange={handleImageUpload}
          />
        </label>
        <span className="ml-4 text-gray-500">Upload doctor picture</span>
      </div>

      {/* Doctor form */}
      <form className="grid grid-cols-2 gap-4 mb-6">
        <div>
          <label className="block mb-2">Doctor's Name</label>
          <input
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Name"
            value={doctorName}
            onChange={(e) => setDoctorName(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-2">Specialty</label>
          <select
            value={specialty}
            onChange={(e) => setSpecialty(e.target.value)}
            className={`w-full p-2 border rounded-md ${specialty === "Specialization" ? 'text-gray-400': 'text-black'}`}
          >
            <option value="Specialization" disabled>
              Specialization
            </option>
            <option value="General Physician">General Physician</option>
            <option value="Gynecologist">Gynecologist</option>
            <option value="Dermatologist">Dermatologist</option>
            <option value="Pediatrician">Pediatrician</option>
            <option value="Neurologist">Neurologist</option>
            <option value="Gastroenterologist">Gastroenterologist</option>
          </select>
        </div>
        <div>
          <label className="block mb-2">Doctor's Email</label>
          <input
            type="email"
            className="w-full p-2 border rounded-md"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-2">Degree</label>
          <input
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Degree"
            value={degree}
            onChange={(e) => setDegree(e.target.value)}
          />
        </div>
        <div>
          {/* Conditionally render "Set Password" or "Change Password" */}
          <label className="block mb-2">
            {doctor ? 'Change Password' : 'Set Password'}
          </label>
          <input
            type="password"
            className="w-full p-2 border rounded-md"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-2">Address</label>
          <input
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-2">Experience</label>
          <input
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="1 Year"
            value={experience}
            onChange={(e) => setExperience(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-2">About Doctor</label>
          <textarea
            className="w-full p-2 border rounded-md"
            placeholder="Write about doctor"
            value={about}
            onChange={(e) => setAbout(e.target.value)}
          />
        </div>
      </form>
      {/* Buttons */}
      <div className="flex gap-3">
        <button
          type="button"
          className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-md"
          onClick={handleSave}
        >
          {doctor ? 'Save Changes' : 'Add Doctor'}
        </button>
        {/* Discard Changes Button */}
        {doctor && (
          <button
            type="button"
            className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-md"
            onClick={onCancel}
          >
            Discard Changes
          </button>
        )}
      </div>
    </section>
  );
}

export default AddDoctorForm;
