// AddFacilityForm.js
import React, { useState, useEffect } from 'react';

function AddFacilityForm({ facilityData }) {
  const [facilityImage, setFacilityImage] = useState(null);
  const [facilityName, setFacilityName] = useState('');
  const [address, setAddress] = useState('');
  const [aboutFacility, setAboutFacility] = useState('');

  useEffect(() => {
    if (facilityData) {
      // Pre-fill the form when facilityData is passed
      setFacilityName(facilityData.name);
      setAddress('Some default address'); // Add real address if available
      setAboutFacility('Some facility description'); // Add real description if available
    }
  }, [facilityData]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setFacilityImage(URL.createObjectURL(file));
  };

  return (
    <section className="flex flex-col bg-white border rounded-lg p-8">
      <div className="flex items-center mb-4">
        <label htmlFor="facilityImage" className="cursor-pointer">
          <div className="w-14 h-14 bg-gray-200 rounded-full flex items-center justify-center">
            {facilityImage ? (
              <img src={facilityImage} alt="Facility" className="w-14 h-14 rounded-full object-cover" />
            ) : (
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                className="w-8 h-8 text-gray-400"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 4.5C13.38 4.5 14.5 3.38 14.5 2C14.5 0.62 13.38 -0.5 12 -0.5C10.62 -0.5 9.5 0.62 9.5 2C9.5 3.38 10.62 4.5 12 4.5ZM5 20.5C5 17 8 14 12 14C16 14 19 17 19 20.5C19 20.92 18.92 21.31 18.79 21.67C17.34 21.18 15.73 21 14 21C11.24 21 8.73 21.27 6.5 21.81C6.18 21.07 6 20.32 6 19.5C6 18.12 5 17.5 5 20.5Z"
                />
              </svg>
            )}
          </div>
          <input
            type="file"
            id="facilityImage"
            accept="image/*"
            className="hidden"
            onChange={handleImageUpload}
          />
        </label>
        <span className="ml-4 text-gray-500">Modify facility picture</span>
      </div>

      <div className="grid grid-cols-1 gap-4 mb-6">
        <div>
          <label htmlFor="facility-name" className="block mb-2">Facility Name</label>
          <input
            id="facility-name"
            name="facility-name"
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Name"
            value={facilityName}
            onChange={(e) => setFacilityName(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="address" className="block mb-2">Address</label>
          <input
            id="address"
            name="address"
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Address 1"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="about-facility" className="block mb-2">About Facility</label>
          <textarea
            id="about-facility"
            name="about-facility"
            rows="3"
            className="w-full p-2 border rounded-md"
            placeholder="Write about facility"
            value={aboutFacility}
            onChange={(e) => setAboutFacility(e.target.value)}
          />
        </div>
      </div>
    </section>
  );
}

export default AddFacilityForm;
