import React, { useState, useEffect } from 'react';
import AddDoctorForm from './AddDoctorForm';
import DoctorGrid from './DoctorGrid';
import Header from './Header';
import Sidebar from './Sidebar';

function ManageDoctors() {
  // State to keep track of screen width
  const [isBelow1390, setIsBelow1390] = useState(false);

  // Check the screen width on initial render and when resized
  useEffect(() => {
    const handleResize = () => {
      setIsBelow1390(window.innerWidth <= 1390);
    };

    // Set the initial state
    handleResize();

    // Attach the event listener for resize
    window.addEventListener('resize', handleResize);

    // Clean up the event listener
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="flex flex-col">
      <Header />
      <main className="flex">
        <Sidebar />
        <section className="flex flex-col items-start mt-0 ml-16 w-full max-w-[1380px]">
          {/* AddDoctorForm and DoctorGrid components with conditional layout */}
          <div className="flex flex-wrap gap-5 w-full mt-5">
            {/* The form will remain full width, but we are ensuring responsiveness */}
            <div className={`flex-auto ${isBelow1390 ? 'w-full' : 'w-3/5'}`}>
              <AddDoctorForm />
            </div>
            <div className={`flex-auto ${isBelow1390 ? 'w-full mt-5' : 'w-2/5'}`}>
              <DoctorGrid />
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default ManageDoctors;
