import React from 'react';
import DoctorCard from './DoctorCard';

function DoctorGrid({ onEdit }) {
  const doctors = [
    {
      name: 'Dr. Richard James',
      specialty: 'General Physician',
      image: 'https://example.com/doctor1.jpg',
      email: 'richard.james@hospital.com',
      degree: 'MBBS',
      address: '123 Main St',
      experience: '10 years',
      about: 'Experienced in general medicine and primary care.',
    },
    // More doctors...
  ];

  return (
    <section className="mt-8">
      <h3 className="text-xl font-medium mb-6">All Doctors</h3>
      <div className="grid grid-cols-4 gap-6">
        {doctors.map((doctor, index) => (
          <div key={index} className="doctor-card">
            <DoctorCard
              name={doctor.name}
              specialty={doctor.specialty}
              image={doctor.image}
            />
            <button
              className="mt-2 px-4 py-2 bg-indigo-500 text-white rounded-md"
              onClick={() => onEdit(doctor)}  // Pass the selected doctor to the onEdit function
            >
              Edit
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default DoctorGrid;
