import React, { useState } from 'react';
import DoctorCard from './DoctorCard';
import AddDoctorForm from './AddDoctorForm';
import Modal from './Modal'; // Assuming you have a Modal component

function DoctorGrid() {
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const doctors = [
    {
      name: 'Dr. Richard James',
      specialty: 'General Physician',
      image: 'https://cdn.builder.io/api/v1/image/assets/TEMP/67ab25dfbe84419bbec844be527f71efc68b5c6cc9d3cbba8d5699717b1e9e0a?placeholderIfAbsent=true&apiKey=8f0d2cc9bf764b679624a1d309a49f82',
      email: 'richard@example.com',
      degree: 'MD',
      experience: '10 years',
      address: '123 Medical St',
      about: 'An experienced physician with over 10 years in the field.',
    },
    // More doctors...
  ];

  const handleEdit = (doctor) => {
    setSelectedDoctor(doctor);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
  };

  const handleSaveDoctor = (updatedDoctor) => {
    console.log('Updated doctor:', updatedDoctor);
    // Update the doctor in your list here (in a real app, you would update the state)
    setIsModalOpen(false);
  };

  return (
    <section className="mt-8">
      <h3 className="text-xl font-medium mb-6">All Doctors</h3>
      <div className="grid grid-cols-4 gap-6">
        {doctors.map((doctor, index) => (
          <DoctorCard
            key={index}
            name={doctor.name}
            specialty={doctor.specialty}
            image={doctor.image}
            onEdit={() => handleEdit(doctor)} // Pass the doctor details to the edit function
          />
        ))}
      </div>

      {isModalOpen && (
        <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
          <AddDoctorForm doctor={selectedDoctor} onSave={handleSaveDoctor} />
        </Modal>
      )}
    </section>
  );
}

export default DoctorGrid;
