import React, { useState } from 'react';
import AddDoctorForm from './AddDoctorForm';
import DoctorGrid from './DoctorGrid';
import Header from './Header';
import Sidebar from './Sidebar';
import Modal from './Modal'; // Assuming you have a Modal component for pop-up

function ManageDoctors() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null); // Stores the doctor being edited
  const [isDiscardModalOpen, setIsDiscardModalOpen] = useState(false);

  // Function to open the modal for editing a doctor
  const handleEditDoctor = (doctor) => {
    setSelectedDoctor(doctor); // Set the doctor to be edited
    setIsModalOpen(true); // Open the modal
  };

  // Function to close the modal
  const handleCloseModal = () => {
    setIsModalOpen(false); // Close the modal
    setSelectedDoctor(null); // Reset the selected doctor
  };

  // Function to save the doctor data (for both adding and editing)
  const handleSaveDoctor = (doctorData) => {
    if (selectedDoctor) {
      // Handle the case for editing an existing doctor
      console.log('Edited Doctor:', doctorData);
      // Perform any necessary updates to your data or state
    } else {
      // Handle the case for adding a new doctor
      console.log('New Doctor:', doctorData);
      // Perform any necessary additions to your data or state
    }
    handleCloseModal(); // Close the modal after saving
  };

  // Function to show discard changes confirmation
  const handleDiscardChanges = () => {
    setIsDiscardModalOpen(true); // Open the discard confirmation modal
  };

  // Function to confirm discard changes
  const handleConfirmDiscard = () => {
    setIsDiscardModalOpen(false); // Close the discard confirmation modal
    handleCloseModal(); // Close the main form modal
  };

  // Function to close discard modal without discarding changes
  const handleCloseDiscardModal = () => {
    setIsDiscardModalOpen(false);
  };

  return (
    <div className="flex-1 flex flex-col min-h-screen">
      <Header />
      <div className="flex w-full min-h-screen bg-gray-50">
        {/* Add responsive width to Sidebar */}
        <Sidebar className="w-[289px] max-w-[289px] max-md:w-[200px] min-w-[150px]" />
        <main className="flex-1 px-8 py-4 w-full">
          {/* Container for the form and grid */}
          <div className="flex flex-col gap-2">
            {/* Add Doctor Form for adding new doctors */}
            <div className="min-w-[700px] max-w-[1000px]">
              <AddDoctorForm onSave={handleSaveDoctor} /> {/* For adding a new doctor */}
            </div>

            {/* Doctor Grid with edit functionality */}
            <div className="min-w-[900px] max-w-[1200px]">
              <DoctorGrid onEdit={handleEditDoctor} /> {/* Pass the handleEditDoctor function */}
            </div>
          </div>
        </main>
      </div>

      {/* Modal to show the AddDoctorForm for editing */}
      <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
        {selectedDoctor && (
          <AddDoctorForm
            doctor={selectedDoctor} // Pass the selected doctor to edit
            onSave={handleSaveDoctor}
            onCancel={handleDiscardChanges} // Pass the discard handler to show discard confirmation
            isEditMode={true} // Pass isEditMode as true for the pop-up form
          />
        )}
      </Modal>

      {/* Discard Confirmation Modal */}
      {isDiscardModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <p className="mb-4">Are you sure you want to discard the unsaved changes?</p>
            <div className="flex justify-end gap-2">
              <button
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md"
                onClick={handleCloseDiscardModal}
              >
                Close
              </button>
              <button
                className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-md"
                onClick={handleConfirmDiscard}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ManageDoctors;
