import React, { useState, useEffect } from 'react';
import AddDoctorForm from './AddDoctorForm';
import DoctorGrid from './DoctorGrid';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import Modal from '../components/Modal';
import axios from 'axios';

function ManageDoctors() {
  const [doctors, setDoctors] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null);

  // Function to fetch doctors from the backend
  const fetchDoctors = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/doctors');
      setDoctors(response.data);
    } catch (error) {
      console.error("Error fetching doctors:", error);
    }
  };

  // Initial fetch when component mounts
  useEffect(() => {
    fetchDoctors();
  }, []);

  const handleEditDoctor = (doctor) => {
    setSelectedDoctor(doctor);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedDoctor(null);
  };

  const handleSaveDoctor = (doctorData) => {
    fetchDoctors(); // Refresh the doctor grid after saving
    handleCloseModal(); // Close the modal after save
  };

  return (
    <div className="flex-1 flex flex-col min-h-screen">
      <Header />
      <div className="flex w-full min-h-screen bg-gray-50">
        <Sidebar className="w-[289px] max-w-[289px] max-md:w-[200px] min-w-[150px]" />
        <main className="flex-1 px-8 py-4 w-full">
          <div className="flex flex-col gap-2">
            <div className="min-w-[700px] max-w-[1000px]">
              {/* Add Doctor Form */}
              <AddDoctorForm onSave={handleSaveDoctor} onDoctorAdded={fetchDoctors} />
            </div>
            <div className="min-w-[900px] max-w-[1200px]">
              {/* Doctor Grid */}
              <DoctorGrid doctors={doctors} onEdit={handleEditDoctor} />
            </div>
          </div>
        </main>
      </div>

      {/* Modal for editing a doctor */}
      {isModalOpen && (
        <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
          {selectedDoctor && (
            <AddDoctorForm
              doctor={selectedDoctor}
              onSave={handleSaveDoctor}
              onCancel={handleCloseModal}
            />
          )}
        </Modal>
      )}
    </div>
  );
}

export default ManageDoctors;
