import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import StatCard from './StatCard';
import DoctorsList from './DoctorList';
import FacilitiesList from './FacilityList';
import axios from 'axios';
import '../styles/tailwind.css';

function Dashboard() {
  // Initialize counts with a loading state or default to 0
  const [doctorCount, setDoctorCount] = useState(0);
  const [appointmentCount, setAppointmentCount] = useState(0);
  const [patientCount, setPatientCount] = useState(0);

  const [isBelow1390, setIsBelow1390] = useState(false);

  // Fetch counts for doctors, appointments, and patients from the backend
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const doctorRes = await axios.get('http://localhost:4000/api/doctors/count');
        const appointmentRes = await axios.get('http://localhost:4000/api/appointments/count');
        const patientRes = await axios.get('http://localhost:4000/api/patients/count');

        setDoctorCount(doctorRes.data.count);
        setAppointmentCount(appointmentRes.data.count);
        setPatientCount(patientRes.data.count);
      } catch (error) {
        console.error("Error fetching counts:", error);
      }
    };

    fetchStats();

    const stats = [
      { icon: "https://cdn.builder.io/api/v1/image/assets/TEMP/645ebcc21dee42475ab18acf5406c5a943ffd9135e81088dfbc355e4a9fe9a87?placeholderIfAbsent=true&apiKey=8f0d2cc9bf764b679624a1d309a49f82", count: doctorCount, label: "Doctors" },
      { icon: "https://cdn.builder.io/api/v1/image/assets/TEMP/bed9b7541659c2d430a307f8e5f21a44d44d0dc1d9446ba2a7cbd611df96bde3?placeholderIfAbsent=true&apiKey=8f0d2cc9bf764b679624a1d309a49f82", count: appointmentCount, label: "Appointments" },
      { icon: "https://cdn.builder.io/api/v1/image/assets/TEMP/fd4001b3ce059345da6e2573f27283b4252a81f91fb09ee7d168ab983a1c21a2?placeholderIfAbsent=true&apiKey=8f0d2cc9bf764b679624a1d309a49f82", count: patientCount, label: "Patients" }
    ];

    // Check screen width on initial render and when resized
    const handleResize = () => {
      setIsBelow1390(window.innerWidth <= 1390);
    };

    handleResize();
    window.addEventListener('resize', handleResize);

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="flex flex-col min-h-screen">
      <Header />
      <main className="flex flex-1">
        <Sidebar className="w-[389px]" />
        <section className="flex flex-col items-start mt-0 w-full max-w-[1380px] ml-5">
          <div className="flex flex-wrap gap-3 pt-5 mt-0">
            {stats.map((stat, index) => (
              <StatCard key={index} {...stat} />
            ))}
          </div>

          <div className={`flex gap-5 w-full mt-5 ${isBelow1390 ? 'flex-col' : ''}`}>
            <DoctorsList />
            <FacilitiesList />
          </div>
        </section>
      </main>
    </div>
  );
}

export default Dashboard;
