import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Dashboard from './Dashboard'; // Assuming Dashboard.js is the main dashboard component
import ManageDoctors from './ManageDoctors'; // The page for managing doctors
import ManageFacilities from './ManageFacilities'; // The page for managing facilities
import Sidebar from './Sidebar'; // Assuming Sidebar is imported here for layout purposes
import Header from './Header'; // Importing the Header component
import Modal from './Modal'; // Importing the Modal component for logout confirmation

function App() {
  const [isLogoutModalOpen, setIsLogoutModalOpen] = useState(false);

  const handleLogoutClick = () => {
    setIsLogoutModalOpen(true); // Open the logout confirmation modal
  };

  const handleCloseLogoutModal = () => {
    setIsLogoutModalOpen(false); // Close the logout confirmation modal
  };

  const handleConfirmLogout = () => {
    console.log('Logging out...');
    // Add your logout logic here, such as redirecting to the login page or clearing session
    setIsLogoutModalOpen(false); // Close the modal after confirming logout
  };

  return (
    <Router>
      <div className="flex">
        <Sidebar /> {/* Sidebar should be present on all pages */}
        <div className="flex-1">
          <Header onLogoutClick={handleLogoutClick} /> {/* Passing logout handler to Header */}
          <main className="p-4">
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/manage-doctors" element={<ManageDoctors />} />
              <Route path="/manage-facilities" element={<ManageFacilities />} />
            </Routes>
          </main>
        </div>

        {/* Logout Confirmation Modal */}
        {isLogoutModalOpen && (
          <Modal isOpen={isLogoutModalOpen} onClose={handleCloseLogoutModal}>
            <div className="p-6 text-center w-[400px] mx-auto">
              <p className="text-lg mb-4">Are you sure you want to logout?</p>
              <div className="flex justify-center gap-4">
                <button
                  className="px-4 py-2 bg-gray-200 hover:bg-gray-300 text-gray-700 rounded-full"
                  onClick={handleCloseLogoutModal}
                >
                  Close
                </button>
                <button
                  className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-full"
                  onClick={handleConfirmLogout}
                >
                  Confirm
                </button>
              </div>
            </div>
          </Modal>
        )}
      </div>
    </Router>
  );
}

export default App;
