import React, { useState } from 'react';
import axios from 'axios';

function DoctorCard({ name, specialty, email, degree, experience, onEdit, onRemove }) {
  const [isRemoveModalOpen, setIsRemoveModalOpen] = useState(false);

  const handleRemoveClick = () => {
    setIsRemoveModalOpen(true);
  };

  const handleCloseRemoveModal = () => {
    setIsRemoveModalOpen(false);
  };

  const handleConfirmRemove = async () => {
    try {
      await axios.delete(`http://localhost:4000/api/doctors/name/${name}`);
      console.log(`Removed doctor: ${name}`);
      onRemove(name); // Trigger the parent component to update the doctor list
    } catch (error) {
      console.error("Error removing doctor:", error);
    } finally {
      setIsRemoveModalOpen(false);
    }
  };

  return (
    <div className="bg-white border rounded-lg p-4 text-center">
      <h3 className="text-lg font-medium">{name}</h3>
      <p className="text-gray-500">{specialty}</p>
      <div className="flex justify-center gap-2 mt-4">
        <button
          className="px-4 py-1 bg-red-500 hover:bg-red-800 text-white rounded-full"
          onClick={handleRemoveClick}
        >
          Remove
        </button>
        <button
          className="px-4 py-1 bg-gray-300 hover:bg-gray-500 text-gray-800 rounded-full"
          onClick={onEdit}
        >
          Edit
        </button>
      </div>

      {isRemoveModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <p className="text-lg mb-4">
              Are you sure you want to remove <strong>{name}</strong>?
            </p>
            <div className="flex justify-end gap-2">
              <button
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-full"
                onClick={handleCloseRemoveModal}
              >
                Close
              </button>
              <button
                className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-full"
                onClick={handleConfirmRemove}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DoctorCard;
