import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import FacilityGrid from './FacilityGrid';
import AddFacilityForm from './AddFacilityForm';

function ManageFacilities() {
  const [selectedFacility, setSelectedFacility] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleEdit = (facility) => {
    setSelectedFacility(facility);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedFacility(null);
  };

  return (
    <div className="flex-1 flex flex-col min-h-screen">
      <Header />
      <div className="flex w-full min-h-screen bg-gray-50">
        <Sidebar />
        <main className="flex-1 px-8 py-4 w-full">
          <div className="flex flex-col gap-6">
            <FacilityGrid onEdit={handleEdit} />
          </div>

          {/* Modal for Add/Edit Facility */}
          {isModalOpen && (
            <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center z-50">
              <div className="bg-white p-6 rounded-lg w-full max-w-[800px]">
                <button
                  className="text-gray-500 absolute top-2 right-2"
                  onClick={handleCloseModal}
                >
                  X
                </button>
                <AddFacilityForm facility={selectedFacility} />
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default ManageFacilities;
