import React, { useState, useEffect } from 'react';
import AddRoom from './AddRoom';  // Import the AddRoom component

function AddFacilityForm({ facility, onCancel }) {
  const [facilityImage, setFacilityImage] = useState(null);
  const [rooms, setRooms] = useState([]);
  const [facilityName, setFacilityName] = useState("");
  const [address, setAddress] = useState("");
  const [about, setAbout] = useState("");
  const [isDiscardModalOpen, setIsDiscardModalOpen] = useState(false); // State for discard confirmation

  useEffect(() => {
    if (facility) {
      // Populate the form fields with the passed facility data
      setFacilityImage(facility.image);
      setFacilityName(facility.name);
      setAddress(facility.address);
      setAbout(facility.about);
      setRooms(facility.rooms || []);
    }
  }, [facility]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setFacilityImage(URL.createObjectURL(file)); // Preview the uploaded image
  };

  const addRoom = () => {
    setRooms([...rooms, { id: rooms.length + 1, specializations: [] }]);
  };

  const removeRoom = (roomId) => {
    setRooms(rooms.filter((room) => room.id !== roomId));
  };

  const handleRoomChange = (roomId, value) => {
    const updatedRooms = rooms.map((room) =>
      room.id === roomId ? { ...room, specializations: value } : room
    );
    setRooms(updatedRooms);
  };

  const handleDiscardChanges = () => {
    setIsDiscardModalOpen(true); // Open the discard confirmation modal
  };

  const handleCloseDiscardModal = () => {
    setIsDiscardModalOpen(false); // Close discard confirmation modal
  };

  const handleConfirmDiscard = () => {
    setIsDiscardModalOpen(false); // Close the discard modal
    if (onCancel) onCancel(); // Call the parent cancel handler to close the main modal
  };

  return (
    <>
      <section className="flex flex-col bg-white border rounded-lg p-8 w-full min-w-[450px] max-w-[800px]">
        {/* Upload Facility Picture */}
        <div className="flex items-center mb-4">
          <label htmlFor="facilityImage" className="cursor-pointer">
            <div className="w-14 h-14 bg-gray-200 rounded-full flex items-center justify-center">
              {facilityImage ? (
                <img src={facilityImage} alt="Facility" className="w-14 h-14 rounded-full object-cover" />
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  className="w-8 h-8 text-gray-400"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 4.5C13.38 4.5 14.5 3.38 14.5 2C14.5 0.62 13.38 -0.5 12 -0.5C10.62 -0.5 9.5 0.62 9.5 2C9.5 3.38 10.62 4.5 12 4.5ZM5 20.5C5 17 8 14 12 14C16 14 19 17 19 20.5C19 20.92 18.92 21.31 18.79 21.67C17.34 21.18 15.73 21 14 21C11.24 21 8.73 21.27 6.5 21.81C6.18 21.07 6 20.32 6 19.5C6 18.12 5 17.5 5 20.5Z"
                  />
                </svg>
              )}
            </div>
            <input
              type="file"
              id="facilityImage"
              accept="image/*"
              className="hidden"
              onChange={handleImageUpload}
            />
          </label>
          <span className="ml-4 text-gray-500">Upload facility picture</span>
        </div>

        {/* Facility form */}
        <form className="grid grid-cols-1 gap-4 mb-6">
          <div>
            <label htmlFor="facility-name" className="block mb-2">Facility Name</label>
            <input
              id="facility-name"
              name="facility-name"
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="Name"
              value={facilityName}
              onChange={(e) => setFacilityName(e.target.value)}
            />
          </div>

          <div>
            <label htmlFor="address" className="block mb-2">Address</label>
            <input
              id="address"
              name="address"
              type="text"
              className="w-full p-2 border rounded-md"
              placeholder="Address 1"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>

          <div>
            <label htmlFor="about-facility" className="block mb-2">About Facility</label>
            <textarea
              id="about-facility"
              name="about-facility"
              rows="3"
              className="w-full p-2 border rounded-md"
              placeholder="Write about facility"
              value={about}
              onChange={(e) => setAbout(e.target.value)}
            />
          </div>

          {/* Dynamically rendered rooms */}
          {rooms.map((room) => (
            <AddRoom
              key={room.id}
              room={room}
              onRemoveRoom={removeRoom}
              onRoomChange={handleRoomChange}
            />
          ))}
        </form>

        {/* Buttons */}
        <div className="flex gap-3">
          <button
            type="button"
            className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-md"
            onClick={addRoom}
          >
            {facility ? 'Save Changes' : 'Add Facility'}
          </button>

          {facility && (
            <button
              type="button"
              className="px-4 py-2 bg-red-500 hover:bg-red-800 text-white rounded-md"
              onClick={handleDiscardChanges} // Trigger discard confirmation modal
            >
              Discard Changes
            </button>
          )}
        </div>
      </section>

      {/* Discard Confirmation Modal */}
      {isDiscardModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <p className="mb-4">Are you sure you want to discard the unsaved changes?</p>
            <div className="flex justify-end gap-2">
              <button
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md"
                onClick={handleCloseDiscardModal}
              >
                Close
              </button>
              <button
                className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-md"
                onClick={handleConfirmDiscard} // Confirm discard and close the form modal
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default AddFacilityForm;
