import React, { useState, useEffect } from 'react';
import AddDoctorForm from './AddDoctorForm';
import DoctorGrid from './DoctorGrid';
import Header from './Header';
import Sidebar from './Sidebar';

function ManageDoctors() {
    // State to keep track of screen width
    const [isBelow1390, setIsBelow1390] = useState(false);

    // Check the screen width on initial render and when resized
    useEffect(() => {
      const handleResize = () => {
        setIsBelow1390(window.innerWidth <= 1390);
      };

      // Set the initial state
      handleResize();

      // Attach the event listener for resize
      window.addEventListener('resize', handleResize);

      // Clean up the event listener
      return () => window.removeEventListener('resize', handleResize);
    }, []);
  return (
    <div className="flex-1 flex flex-col min-h-screen">
      <Header />
      <div className="flex w-full min-h-screen bg-gray-50">
        {/* Add responsive width to Sidebar */}
        <Sidebar className="w-[289px] max-w-[289px] max-md:w-[200px] min-w-[150px]" /> 
        <main className="flex-1 px-8 py-4 w-full">
          {/* Container for the form and grid */}
          <div className="flex flex-col gap-2">
            <div className="min-w-[700px] max-w-[1000px]"> {/* Set min and max width for AddDoctorForm */}
              <AddDoctorForm />
            </div>
            <div className="min-w-[900px] max-w-[1200px]"> {/* Set min and max width for DoctorGrid */}
              <DoctorGrid />
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

export default ManageDoctors;
