import React, { useState, useEffect } from 'react';
import AddRoom from './AddRoom';  // Reuse the AddRoom component

function EditFacilityForm({ facilityId }) {
  const [facility, setFacility] = useState(null); // Store selected facility data
  const [rooms, setRooms] = useState([]); // Dynamically track rooms

  // Mock facility data (in a real application, this could come from a backend API)
  const facilityData = [
    {
      id: 1,
      name: "Facility 1",
      address1: "123 Street",
      address2: "City, State",
      about: "This is a modern healthcare facility.",
      rooms: [
        { id: 1, specialization: "General Physician" },
        { id: 2, specialization: "Dermatologist" }
      ]
    },
    {
      id: 2,
      name: "Facility 2",
      address1: "456 Avenue",
      address2: "City, State",
      about: "Another healthcare facility.",
      rooms: [{ id: 1, specialization: "Pediatrician" }]
    }
  ];

  useEffect(() => {
    // Retrieve facility by its ID
    const selectedFacility = facilityData.find(facility => facility.id === facilityId);
    setFacility(selectedFacility);
    setRooms(selectedFacility?.rooms || []);
  }, [facilityId]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setFacility({
      ...facility,
      image: URL.createObjectURL(file) // Update facility image preview
    });
  };

  // Function to add a new room
  const addRoom = () => {
    setRooms([...rooms, { id: rooms.length + 1, specialization: '' }]);
  };

  // Function to remove a room
  const removeRoom = (roomId) => {
    setRooms(rooms.filter((room) => room.id !== roomId));
  };

  // Function to update room specialization
  const handleRoomChange = (roomId, value) => {
    const updatedRooms = rooms.map((room) =>
      room.id === roomId ? { ...room, specialization: value } : room
    );
    setRooms(updatedRooms);
  };

  if (!facility) return <p>Loading...</p>;

  return (
    <section className="flex flex-col bg-white border rounded-lg p-8 w-full min-w-[450px] max-w-[800px]">
      {/* Upload Facility Picture */}
      <div className="flex items-center mb-4">
        <label htmlFor="facilityImage" className="cursor-pointer">
          <div className="w-14 h-14 bg-gray-200 rounded-full flex items-center justify-center">
            {facility.image ? (
              <img src={facility.image} alt="Facility" className="w-14 h-14 rounded-full object-cover" />
            ) : (
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                className="w-8 h-8 text-gray-400"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 4.5C13.38 4.5 14.5 3.38 14.5 2C14.5 0.62 13.38 -0.5 12 -0.5C10.62 -0.5 9.5 0.62 9.5 2C9.5 3.38 10.62 4.5 12 4.5ZM5 20.5C5 17 8 14 12 14C16 14 19 17 19 20.5C19 20.92 18.92 21.31 18.79 21.67C17.34 21.18 15.73 21 14 21C11.24 21 8.73 21.27 6.5 21.81C6.18 21.07 6 20.32 6 19.5C6 18.12 5 17.5 5 20.5Z"
                />
              </svg>
            )}
          </div>
          <input
            type="file"
            id="facilityImage"
            accept="image/*"
            className="hidden"
            onChange={handleImageUpload}
          />
        </label>
        <span className="ml-4 text-gray-500">Modify facility picture</span>
      </div>

      {/* Facility form */}
      <form className="grid grid-cols-1 gap-4 mb-6">
        <div>
          <label htmlFor="facility-name" className="block mb-2">Facility Name</label>
          <input
            id="facility-name"
            name="facility-name"
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Name"
            value={facility.name}
            onChange={(e) => setFacility({ ...facility, name: e.target.value })}
          />
        </div>

        <div>
          <label htmlFor="address" className="block mb-2">Address</label>
          <input
            id="address"
            name="address"
            type="text"
            className="w-full p-2 border rounded-md"
            placeholder="Address 1"
            value={facility.address1}
            onChange={(e) => setFacility({ ...facility, address1: e.target.value })}
          />
          <input
            name="address-2"
            type="text"
            className="mt-2 w-full p-2 border rounded-md"
            placeholder="Address 2"
            value={facility.address2}
            onChange={(e) => setFacility({ ...facility, address2: e.target.value })}
          />
        </div>

        <div>
          <label htmlFor="about-facility" className="block mb-2">About Facility</label>
          <textarea
            id="about-facility"
            name="about-facility"
            rows="3"
            className="w-full p-2 border rounded-md"
            placeholder="Write about facility"
            value={facility.about}
            onChange={(e) => setFacility({ ...facility, about: e.target.value })}
          />
        </div>

        {/* Dynamically rendered rooms */}
        {rooms.map((room) => (
          <AddRoom
            key={room.id}
            room={room}
            onRemoveRoom={removeRoom}
            onRoomChange={handleRoomChange}
          />
        ))}
      </form>

      {/* Buttons */}
      <div className="flex gap-3">
        <button
          type="button"
          className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-full"
          onClick={addRoom}
        >
          Add Room
        </button>
        <button
          type="submit"
          className="px-4 py-2 bg-indigo-500 hover:bg-indigo-600 text-white rounded-full"
        >
          Save Changes
        </button>
      </div>
    </section>
  );
}

export default EditFacilityForm;
