import React, { useState } from 'react';

function AddRoom({ room, onRemoveRoom }) {
  const [selectedSpecialty, setSelectedSpecialty] = useState('Specialization');

  const handleSpecialtyChange = (event) => {
    setSelectedSpecialty(event.target.value);
  };

  return (
    <div className="flex items-start gap-3 mb-4">
      <div className="flex-1">
        <label htmlFor={`room-${room.id}`} className="block mb-2">
          Room {room.id}'s Specialization
        </label>
        <select
          value={selectedSpecialty}
          onChange={handleSpecialtyChange}
          className={`w-full p-2 border rounded-md ${selectedSpecialty === "Specialization" ? 'text-gray-400' : 'text-black'}`}
        >
          <option value="Specialization" disabled>Specialization</option>
          <option value="General Physician">General Physician</option>
          <option value="Gynecologist">Gynecologist</option>
          <option value="Dermatologist">Dermatologist</option>
          <option value="Pediatrician">Pediatrician</option>
          <option value="Neurologist">Neurologist</option>
          <option value="Gastroenterologist">Gastroenterologist</option>
        </select>
      </div>
      <button
        type="button"
        className="mt-4 px-4 py-2 bg-red-500 text-white rounded-full hover:bg-red-600"
        onClick={() => onRemoveRoom(room.id)}
      >
        Remove Room
      </button>
    </div>
  );
}

export default AddRoom;
